let counter=0;const modals=document.getElementsByClassName("modal"),overlay=document.getElementsByClassName("overlay").item(0),modalButtons=document.getElementsByClassName("modal__footer-button");document.querySelector(".button--send-review").addEventListener("click",(e=>{e.preventDefault(),showModal()}));for(let e of modalButtons)e.addEventListener("click",(()=>{closeModal()}));function showModal(){modals[counter%2].style.display="block",overlay.style.display="block",counter++}function closeModal(){for(let e of modals)e.style.display="none";overlay.style.display="none"}